import React, { useState, useContext } from "react";
import AuthContext from "../../AuthContext";

import { Formik } from "formik";
import * as Yup from "yup";

import { confirmAlert } from "react-confirm-alert";
import "react-confirm-alert/src/react-confirm-alert.css";

const validationSchema = Yup.object().shape({
  firstName: Yup.string(),
  lastName: Yup.string(),
});

const UserProfile = () => {
  const [isProfileUpdate, setisProfileUpdate] = useState(false);

  const userData = useContext(AuthContext);
  console.log(userData);
  const profileUpdated = () => {
    confirmAlert({
      title: "Profile Updated",

      buttons: [
        {
          label: "ok",
          onClick: () => {
            setisProfileUpdate(false);
          },
        },
      ],
    });
  };

  const UpdateUserHandler = (
    firstName,
    lastName,
    phone,
    profession,
    address,
    about
  ) => {
    console.log(firstName, lastName, phone, profession, address, about);
  };
  return (
    <div>
      <div className="row mb-none-30" style={{ padding: "0px 30px" }}>
        <div className="col-md-6">
          <div className="blog-list mb-30">
            <div className="post-tags">
              <h3>Personal</h3>
            </div>
          </div>
        </div>
        <div className="col-md-6">
          <div className="blog-list mb-30" style={{ float: "right" }}>
            <div className="post-tags">
              <span
                onClick={() => setisProfileUpdate(true)}
                className="updateprofilebutton"
                href="#0"
              >
                {isProfileUpdate ? "" : "Update"}
              </span>
            </div>
          </div>
        </div>
        {/* blog-item end */}
      </div>
      <div></div>

      {isProfileUpdate ? (
        <div className="row mb-none-30">
          <div className="col-md-12">
            <div className="blog-item  mb-30">
              <div className="content">
                <Formik
                  initialValues={{
                    firstName: "",
                    lastName: "",
                    phone: "",
                    profession: "",
                    address: "",
                    about: "",
                  }}
                  validate={(values) => {
                    const errors = {};
                    if (!values.firstName) {
                      errors.firstName = "First Name is Required";
                    } else if (!values.lastName) {
                      errors.lastName = "Last Name is Required";
                    }
                    return errors;
                  }}
                  onSubmit={(values, actions) => {
                    UpdateUserHandler(
                      values.firstName,
                      values.lastName,
                      values.phone,
                      values.profession,
                      values.address,
                      values.about
                    );
                    actions.resetForm();
                    profileUpdated();
                  }}
                  validationSchema={validationSchema}
                >
                  {({
                    handleChange,
                    handleSubmit,
                    values,
                    errors,
                    touched,
                    isSubmitting,
                    handleBlur,
                  }) => (
                    <form className="signin-form" onSubmit={handleSubmit}>
                      <div className="form-group">
                        <label for="firstName">First Name*</label>
                        <input
                          type="text"
                          name="firstName"
                          className="form-control"
                          placeholder="First Name"
                          onBlur={handleBlur}
                          value={values.firstName}
                          onChange={handleChange}
                        />
                        <p
                          style={{
                            color: "red",
                            fontSize: "12px",
                            fontWeight: "bold",
                          }}
                        >
                          {errors.firstName &&
                            touched.firstName &&
                            errors.firstName}
                        </p>
                      </div>
                      <div className="form-group">
                        <label for="lastName">Last Name</label>
                        <input
                          type="text"
                          name="lastName"
                          className="form-control"
                          placeholder="Last Name"
                          onBlur={handleBlur}
                          value={values.lastName}
                          onChange={handleChange}
                        />
                        <p
                          style={{
                            color: "red",
                            fontSize: "12px",
                            fontWeight: "bold",
                          }}
                        >
                          {errors.lastName &&
                            touched.lastName &&
                            errors.lastName}
                        </p>
                      </div>

                      <div className="form-group">
                        <label for="Phone">Phone</label>
                        <input
                          type="text"
                          name="phone"
                          className="form-control"
                          placeholder="Phone Number"
                          value={values.phone}
                          onChange={handleChange}
                        />
                      </div>

                      <div className="form-group">
                        <label for="profession">Profession</label>
                        <input
                          type="text"
                          name="profession"
                          className="form-control"
                          placeholder="Your Profession"
                          value={values.profession}
                          onChange={handleChange}
                        />
                      </div>

                      <div className="form-group">
                        <label for="address">Address</label>
                        <textarea
                          name="address"
                          className="form-control"
                          placeholder="Your Address"
                          value={values.address}
                          onChange={handleChange}
                        />
                      </div>
                      <div>
                        <label for="about">About</label>
                        <textarea
                          name="about"
                          className="form-control"
                          placeholder="About Yourself"
                          value={values.about}
                          onChange={handleChange}
                        />
                      </div>

                      <button
                        type="submit"
                        className="btn btn-primary btn-hover"
                      >
                        Update
                      </button>
                    </form>
                  )}
                </Formik>
              </div>
            </div>
          </div>
        </div>
      ) : (
        <div className="row mb-none-30">
          <div className="col-md-12">
            <div className="blog-item  mb-30">
              <div className="content" style={{ display: "flex" }}>
                <div className="col-md-6">
                  <p className="profilelabel">
                    Name <span style={{ margin: "10px" }}>:</span>
                    <span className="profiledata">Fahim Ferdous</span>
                  </p>
                  <p className="profilelabel">
                    Phone<span style={{ margin: "10px" }}>:</span>{" "}
                    <span className="profiledata">01744353576</span>
                  </p>
                  <p className="profilelabel">
                    Address
                    <span style={{ margin: "10px" }}>:</span>{" "}
                    <span className="profiledata">Dhaka</span>
                  </p>
                </div>

                <div className="col-md-6">
                  <p className="profilelabel">
                    Profession
                    <span style={{ margin: "10px" }}>:</span>{" "}
                    <span className="profiledata">Service Holder</span>
                  </p>
                  <p className="profilelabel">
                    About<span style={{ margin: "10px" }}>:</span>{" "}
                    <span className="profiledata">About</span>
                  </p>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default UserProfile;
