import React, { useState, useContext } from "react";
import AuthContext from "../../AuthContext";

import { Formik, FieldArray } from "formik";
import * as Yup from "yup";

import { confirmAlert } from "react-confirm-alert";
import "react-confirm-alert/src/react-confirm-alert.css";

const validationSchema = Yup.object().shape({
  firstName: Yup.string(),
  lastName: Yup.string(),
});

const UserProfile = () => {
  const [isProfileUpdate, setisProfileUpdate] = useState(false);
  const [isProfessionUpdate, setisProfessionUpdate] = useState(false);

  const userData = useContext(AuthContext);
  console.log(userData.user);
  const profileUpdated = () => {
    confirmAlert({
      title: "Profile Updated",

      buttons: [
        {
          label: "ok",
          onClick: () => {
            setisProfileUpdate(false);
          },
        },
      ],
    });
  };

  const UpdateUserHandler = (
    firstName,
    lastName,
    phone,
    profession,
    address,
    about
  ) => {
    console.log(firstName, lastName, phone, profession, address, about);
  };
  return (
    <div>
      <div className="row mb-none-30" style={{ padding: "0px 17px" }}>
        <div className="col-md-6">
          <div className="blog-list mb-30">
            <div className="post-tags">
              <h3>Personal</h3>
            </div>
          </div>
        </div>
        <div className="col-md-6">
          <div className="blog-list mb-30" style={{ float: "right" }}>
            <div className="post-tags">
              <span
                onClick={() => setisProfileUpdate(true)}
                className="updateprofilebutton"
                href="#0"
              >
                {isProfileUpdate ? "" : "Update"}
              </span>
            </div>
          </div>
        </div>
        {/* blog-item end */}
      </div>
      <div></div>

      {isProfileUpdate ? (
        <div className="row mb-none-30">
          <div className="col-md-12">
            <div className="blog-item  mb-30">
              <div className="content" style={{ padding: "2%" }}>
                <Formik
                  initialValues={{
                    firstName: "",
                    lastName: "",
                    phone: "",
                    profession: "",
                    address: "",
                    about: "",
                  }}
                  validate={(values) => {
                    const errors = {};
                    if (!values.firstName) {
                      errors.firstName = "First Name is Required";
                    } else if (!values.lastName) {
                      errors.lastName = "Last Name is Required";
                    }
                    return errors;
                  }}
                  onSubmit={(values, actions) => {
                    UpdateUserHandler(
                      values.firstName,
                      values.lastName,
                      values.phone,
                      values.profession,
                      values.address,
                      values.about
                    );
                    actions.resetForm();
                    profileUpdated();
                  }}
                  validationSchema={validationSchema}
                >
                  {({
                    handleChange,
                    handleSubmit,
                    values,
                    errors,
                    touched,
                    isSubmitting,
                    handleBlur,
                  }) => (
                    <form className="signin-form" onSubmit={handleSubmit}>
                      <div className="row">
                        <div className="col-md-6 form-group">
                          <label for="firstName">First Name*</label>
                          <input
                            type="text"
                            name="firstName"
                            className="form-control"
                            placeholder="First Name"
                            onBlur={handleBlur}
                            value={values.firstName}
                            onChange={handleChange}
                          />
                          <p
                            style={{
                              color: "red",
                              fontSize: "12px",
                              fontWeight: "bold",
                            }}
                          >
                            {errors.firstName &&
                              touched.firstName &&
                              errors.firstName}
                          </p>
                        </div>
                        <div className="col-md-6 form-group">
                          <label for="lastName">Last Name</label>
                          <input
                            type="text"
                            name="lastName"
                            className="form-control"
                            placeholder="Last Name"
                            onBlur={handleBlur}
                            value={values.lastName}
                            onChange={handleChange}
                          />
                          <p
                            style={{
                              color: "red",
                              fontSize: "12px",
                              fontWeight: "bold",
                            }}
                          >
                            {errors.lastName &&
                              touched.lastName &&
                              errors.lastName}
                          </p>
                        </div>
                      </div>

                      <div className="row">
                        <div className="col-md-6 form-group">
                          <label for="Phone">Primary Phone </label>
                          <input
                            type="text"
                            name="phone"
                            className="form-control"
                            placeholder="Phone Number"
                            value={values.phone}
                            onChange={handleChange}
                          />
                        </div>
                        <div className="col-md-6 form-group">
                          <label for="Phone">Alternate Phone </label>
                          <input
                            type="text"
                            name="phone"
                            className="form-control"
                            placeholder="Phone Number"
                            value={values.phone}
                            onChange={handleChange}
                          />
                        </div>
                      </div>

                      <div className="row">
                        <div className="col-md-6 form-group">
                          <label for="profession">Alternate Email</label>
                          <input
                            type="text"
                            name="profession"
                            className="form-control"
                            placeholder="Your Profession"
                            value={values.profession}
                            onChange={handleChange}
                          />
                        </div>
                        <div className="col-md-6 form-group">
                          <label for="profession">Birth Date</label>
                          <input
                            type="text"
                            name="profession"
                            className="form-control"
                            placeholder="Your Profession"
                            value={values.profession}
                            onChange={handleChange}
                          />
                        </div>
                      </div>
                      <div className="row">
                        <div className="col-md-6 form-group">
                          <label for="profession">Gender</label>
                          <select
                            name="color"
                            className="form-control"
                            value={values.gender}
                            onChange={handleChange}
                            onBlur={handleBlur}
                            style={{ display: "block" }}
                          >
                            <option value="" label="Select" />
                            <option value="Male" label="Male" />
                            <option value="Female" label="Female" />
                            <option value="Other" label="Other" />
                          </select>
                        </div>
                        <div className="col-md-6 form-group">
                          <label for="profession">Marital Status</label>
                          <select
                            name="color"
                            className="form-control"
                            value={values.gender}
                            onChange={handleChange}
                            onBlur={handleBlur}
                            style={{ display: "block" }}
                          >
                            <option value="" label="Select" />
                            <option value="Married" label="Married" />
                            <option value="Unmarried" label="Unmarried" />
                          </select>
                        </div>
                      </div>

                      <div className="form-group">
                        <label for="address">Address</label>
                        <textarea
                          name="address"
                          className="form-control"
                          placeholder="Your Address"
                          value={values.address}
                          onChange={handleChange}
                        />
                      </div>
                      <div className="form-group">
                        <label for="about">Bio</label>
                        <textarea
                          name="about"
                          className="form-control"
                          placeholder="About Yourself"
                          value={values.about}
                          onChange={handleChange}
                        />
                      </div>

                      <button
                        type="submit"
                        className="btn btn-primary btn-hover"
                      >
                        Update
                      </button>
                    </form>
                  )}
                </Formik>
              </div>
            </div>
          </div>
        </div>
      ) : (
        <div className="row mb-none-30">
          <div className="col-md-12">
            <div className="blog-item  mb-30">
