import React, { useState } from "react";

import { Modal, Button } from "react-bootstrap";
import AccountChangeModal from "../components/AccountChangeModal";

const UserAccount = () => {
  const [showAccountModal, setshowAccountModal] = useState(false);
  const [showAccountChangeModal, setshowAccountChangeModal] = useState(false);
  return (
    <div>
      <div className="row mb-none-30">
        <div className="col-md-12">
          <Modal
            show={showAccountModal}
            onHide={() => {
              setshowAccountModal(false);
            }}
          >
            <Modal.Header closeButton>
              <Modal.Title>Account Type</Modal.Title>
            </Modal.Header>
            <Modal.Body>
              <p>
                Current Subscription:{" "}
                <span className="updateprofilebutton">Bigenner</span>
              </p>
            </Modal.Body>
            <Modal.Footer>
              <Button
                variant="primary"
                onClick={() => {
                  setshowAccountChangeModal(true);
                }}
              >
                Upgrade
              </Button>
            </Modal.Footer>
          </Modal>
          <div
            className="blog-item blog-list mb-30"
            style={{ display: "flex" }}
          >
            <div className=" col-md-6">
              {/* <span
                onClick={() => {
                  setshowAccountModal(true);
                }}
                className="updateprofilebutton"
                href="#0"
              >
                Account type
              </span> */}
            </div>
            <div className=" col-md-6">
              <AccountChangeModal
                showAcountChange={showAccountChangeModal}
                HideAccountModal={() => {
                  setshowAccountChangeModal(false);
                  setshowAccountModal(false);
                }}
              />
              <span
                onClick={() => {}}
                className="updateprofilebutton"
                href="#0"
              >
                Service Preferences
              </span>
            </div>
          </div>
        </div>
        {/* blog-item end */}
      </div>
    </div>
  );
};

export default UserAccount;
