import React, { useState, useContext } from "react";
import AuthContext from "../../AuthContext";

import { Formik, FieldArray } from "formik";
import * as Yup from "yup";

import { confirmAlert } from "react-confirm-alert";
import "react-confirm-alert/src/react-confirm-alert.css";

const validationSchema = Yup.object().shape({
  firstName: Yup.string(),
  lastName: Yup.string(),
});

const UserProfile = () => {
  const [isProfileUpdate, setisProfileUpdate] = useState(false);
  const [isProfessionUpdate, setisProfessionUpdate] = useState(false);
  const [isAcademicsUpdate, setisAcademicsUpdate] = useState(false);

  const userData = useContext(AuthContext);
  console.log(userData.user);
  const profileUpdated = () => {
    confirmAlert({
      title: "Profile Updated",

      buttons: [
        {
          label: "ok",
          onClick: () => {
            setisProfileUpdate(false);
          },
        },
      ],
    });
  };

  const UpdateUserHandler = (
    firstName,
    lastName,
    phone,
    profession,
    address,
    about
  ) => {
    console.log(firstName, lastName, phone, profession, address, about);
  };
  return (
    <div>
      {/* Personal Heading Start */}
      <div className="row mb-none-30">
        <div className="col-md-12 ">
          <div className="blog-item mb-30" style={{ marginTop: "1%" }}>
            <div
              className="content promoBg "
              style={{ display: "flex", padding: "2% 2%" }}
            >
              <p className="col-md-11 promoText">Personal</p>
              <span
                className="promoselectionButton"
                href="#0"
                onClick={() => setisProfileUpdate(true)}
              >
                <i
                  className="fa fa-pencil-square-o promoText"
                  aria-hidden="true"
                  style={{ paddingTop: "1%", fontSize: "28px" }}
                ></i>
              </span>
            </div>
          </div>
        </div>
      </div>

      {/* Personal Heading End */}

      {isProfileUpdate ? (
        <div className="row mb-none-30">
          <div className="col-md-12">
            <div className="blog-item  mb-30">
              <div className="content" style={{ padding: "2%" }}>
                <Formik
                  initialValues={{
                    firstName: "",
                    lastName: "",
                    phone: "",
                    profession: "",
                    address: "",
                    about: "",
                  }}
                  validate={(values) => {
                    const errors = {};
                    if (!values.firstName) {
                      errors.firstName = "First Name is Required";
                    } else if (!values.lastName) {
                      errors.lastName = "Last Name is Required";
                    }
                    return errors;
                  }}
                  onSubmit={(values, actions) => {
                    UpdateUserHandler(
                      values.firstName,
                      values.lastName,
                      values.phone,
                      values.profession,
                      values.address,
                      values.about
                    );
                    actions.resetForm();
                    profileUpdated();
                  }}
                  validationSchema={validationSchema}
                >
                  {({
                    handleChange,
                    handleSubmit,
                    values,
                    errors,
                    touched,
                    isSubmitting,
                    handleBlur,
                  }) => (
                    <form className="signin-form" onSubmit={handleSubmit}>
                      <div className="row">
                        <div className="col-md-6 form-group">
                          <label for="firstName">First Name*</label>
                          <input
                            type="text"
                            name="firstName"
                            className="form-control"
                            placeholder="First Name"
                            onBlur={handleBlur}
                            value={values.firstName}
                            onChange={handleChange}
                          />
                          <p
                            style={{
                              color: "red",
                              fontSize: "12px",
                              fontWeight: "bold",
                            }}
                          >
                            {errors.firstName &&
                              touched.firstName &&
                              errors.firstName}
                          </p>
                        </div>
                        <div className="col-md-6 form-group">
                          <label for="lastName">Last Name</label>
                          <input
                            type="text"
                            name="lastName"
                            className="form-control"
                            placeholder="Last Name"
                            onBlur={handleBlur}
                            value={values.lastName}
                            onChange={handleChange}
                          />
                          <p
                            style={{
                              color: "red",
                              fontSize: "12px",
                              fontWeight: "bold",
                            }}
                          >
                            {errors.lastName &&
                              touched.lastName &&
                              errors.lastName}
                          </p>
                        </div>
                      </div>

                      <div className="row">
                        <div className="col-md-6 form-group">
                          <label for="Phone">Primary Phone </label>
                          <input
                            type="text"
                            name="phone"
                            className="form-control"
                            placeholder="Phone Number"
                            value={values.phone}
                            onChange={handleChange}
                          />
                        </div>
                        <div className="col-md-6 form-group">
                          <label for="Phone">Alternate Phone </label>
                          <input
                            type="text"
                            name="phone"
                            className="form-control"
                            placeholder="Phone Number"
                            value={values.phone}
                            onChange={handleChange}
                          />
                        </div>
                      </div>

                      <div className="row">
                        <div className="col-md-6 form-group">
                          <label for="profession">Alternate Email</label>
                          <input
                            type="text"
                            name="profession"
                            className="form-control"
                            placeholder="Your Profession"
                            value={values.profession}
                            onChange={handleChange}
                          />
                        </div>
                        <div className="col-md-6 form-group">
                          <label for="profession">Birth Date</label>
                          <input
                            type="text"
                            name="profession"
                            className="form-control"
                            placeholder="Your Profession"
                            value={values.profession}
                            onChange={handleChange}
                          />
                        </div>
                      </div>
                      <div className="row">
                        <div className="col-md-6 form-group">
                          <label for="profession">Gender</label>
                          <select
                            name="color"
                            className="form-control"
                            value={values.gender}
                            onChange={handleChange}
                            onBlur={handleBlur}
                            style={{ display: "block" }}
                          >
                            <option value="" label="Select" />
                            <option value="Male" label="Male" />
                            <option value="Female" label="Female" />
                            <option value="Other" label="Other" />
                          </select>
                        </div>
                        <div className="col-md-6 form-group">
                          <label for="profession">Marital Status</label>
                          <select
                            name="color"
                            className="form-control"
                            value={values.gender}
                            onChange={handleChange}
                            onBlur={handleBlur}
                          >
                            <option value="" label="Select" />
                            <option value="Married" label="Married" />
                            <option value="Unmarried" label="Unmarried" />
                          </select>
                        </div>
                      </div>

                      <div className="form-group">
                        <label for="address">Address</label>
                        <textarea
                          name="address"
                          className="form-control"
                          placeholder="Your Address"
                          value={values.address}
                          onChange={handleChange}
                        />
                      </div>
                      <div className="form-group">
                        <label for="about">Bio</label>
                        <textarea
                          name="about"
                          className="form-control"
                          placeholder="About Yourself"
                          value={values.about}
                          onChange={handleChange}
                        />
                      </div>

                      <button
                        type="submit"
                        className="btn btn-primary btn-hover"
                      >
                        Update
                      </button>
                    </form>
                  )}
                </Formik>
              </div>
            </div>
          </div>
        </div>
      ) : (
        <div className="row mb-none-30">
          <div className="col-md-12">
            <div className="blog-item  mb-30">
              <div className="content" style={{ display: "flex" }}>
                <div className="col-md-6">
                  <p className="profilelabel">
                    First Name <span style={{ margin: "10px" }}>:</span>
                    <span className="profiledata">Fahim</span>
                  </p>
                  <p className="profilelabel">
                    Primary Phone <span style={{ margin: "10px" }}>:</span>{" "}
                    <span className="profiledata">01744353576</span>
                  </p>
                  <p className="profilelabel">
                    Primary Email
                    <span style={{ margin: "10px" }}>:</span>{" "}
                    <span className="profiledata">fahim@gmail.com</span>
                  </p>
                  <p className="profilelabel">
                    Gender
                    <span style={{ margin: "10px" }}>:</span>{" "}
                    <span className="profiledata">Male</span>
                  </p>
                  <p className="profilelabel">
                    Marital Status
                    <span style={{ margin: "10px" }}>:</span>{" "}
                    <span className="profiledata">Married</span>
                  </p>
                </div>

                <div className="col-md-6">
                  <p className="profilelabel">
                    Last Name
                    <span style={{ margin: "10px" }}>:</span>{" "}
                    <span className="profiledata">Ferdous</span>
                  </p>
                  <p className="profilelabel">
                    Phone (Other)<span style={{ margin: "10px" }}>:</span>{" "}
                    <span className="profiledata">01829640972</span>
                  </p>
                  <p className="profilelabel">
                    Email (Other)<span style={{ margin: "10px" }}>:</span>{" "}
                    <span className="profiledata">fahim@msn.com</span>
                  </p>
                  <p className="profilelabel">
                    Birthday<span style={{ margin: "10px" }}>:</span>{" "}
                    <span className="profiledata">15/05/1992</span>
                  </p>
                  <p className="profilelabel">
                    Address<span style={{ margin: "10px" }}>:</span>{" "}
                    <span className="profiledata">Khilkhet, Dhaka</span>
                  </p>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Professional Heading Start */}
      <div className="row mb-none-30">
        <div className="col-md-12 ">
          <div className="blog-item mb-30" style={{ marginTop: "1%" }}>
            <div
              className="content promoBg "
              style={{ display: "flex", padding: "2% 2%" }}
            >
              <p className="col-md-11 promoText">Professional</p>
              <span
                className="promoselectionButton"
                href="#0"
                onClick={() => setisProfessionUpdate(true)}
              >
                <i
                  className="fa fa-pencil-square-o promoText"
                  aria-hidden="true"
                  style={{ paddingTop: "1%", fontSize: "28px" }}
                ></i>
              </span>
            </div>
          </div>
        </div>
      </div>

      {/* Professional Heading End */}
      {/* Professional Info Start*/}

      {isProfessionUpdate ? (
        <div className="row mb-none-30">
          <div className="col-md-12">
            <div className="blog-item  mb-30">
              <div className="content" style={{ padding: "2%" }}>
                <Formik
                  initialValues={{
                    professionClass: "",
                    experience: [
                      {
                        org: "",
                        industry: "",
                        role: "",
                        country: "",
                        from: "",
                        to: "",
                      },
                    ],
                    activeMembership: [{ name: "", position: "", from: "" }],
                    awards: [{ name: "", body: "", from: "" }],
                  }}
                  validate={(values) => {
                    const errors = {};
                    if (!values.firstName) {
                      errors.firstName = "First Name is Required";
                    } else if (!values.lastName) {
                      errors.lastName = "Last Name is Required";
                    }
                    return errors;
                  }}
                  onSubmit={(values, actions) => {
                    UpdateUserHandler(
                      values.firstName,
                      values.lastName,
                      values.phone,
                      values.profession,
                      values.address,
                      values.about
                    );
                    actions.resetForm();
                    profileUpdated();
                  }}
                  validationSchema={validationSchema}
                >
                  {({
                    handleChange,
                    handleSubmit,
                    values,
                    errors,
                    touched,
                    isSubmitting,
                    handleBlur,
                  }) => (
                    <form className="signin-form" onSubmit={handleSubmit}>
                      <div className="form-group">
                        <label className="professionFormLebel" for="address">
                          Profession Class
                        </label>
                        <input
                          type="text"
                          name="firstName"
                          className="form-control"
                          placeholder="Your Profession"
                          onBlur={handleBlur}
                          value={values.firstName}
                          onChange={handleChange}
                        />
                      </div>
                      <div>
                        <FieldArray name="experience">
                          {(fieldArrayProps) => (
                            <>
                              <div>
                                <div>
                                  <label
                                    className="professionFormLebel col-md-11"
                                    style={{ padding: "0px" }}
                                  >
                                    Experience
                                  </label>

                                  <span
                                    onClick={() => {
                                      fieldArrayProps.push({
                                        org: "",
                                        industry: "",
                                        role: "",
                                        country: "",
                                        from: "",
                                        to: "",
                                      });
                                    }}
                                    className="updateprofilebutton"
                                    href="#0"
                                    style={{
                                      fontSize: "24px",
                                      margin: "0% 0% 0% 4%",
                                      padding: "0px 15px",
                                    }}
                                  >
                                    +
                                  </span>
                                </div>

                                {values.experience.map((value, index) => (
                                  <>
                                    <div className="row">
                                      <div className="col-md-6 form-group">
                                        <label for="firstName">
                                          Organization
                                        </label>
                                        <input
                                          type="text"
                                          name={`experience.${index}.org`}
                                          className="form-control"
                                          placeholder="Organization"
                                          onBlur={handleBlur}
                                          value={values.firstName}
                                          onChange={handleChange}
                                        />
                                      </div>
                                      <div className="col-md-6 form-group">
                                        <label for="lastName">Industry</label>
                                        <input
                                          type="text"
                                          name={`experience.${index}.industry`}
                                          className="form-control"
                                          placeholder="Type of industry"
                                          onBlur={handleBlur}
                                          value={values.lastName}
                                          onChange={handleChange}
                                        />
                                      </div>
                                    </div>

                                    <div className="row">
                                      <div className="col-md-6 form-group">
                                        <label for="Phone">Role </label>
                                        <input
                                          type="text"
                                          name={`experience.${index}.role`}
                                          className="form-control"
                                          placeholder="Designation"
                                          value={values.phone}
                                          onChange={handleChange}
                                        />
                                      </div>
                                      <div className="col-md-6 form-group">
                                        <label for="Phone">Country </label>
                                        <input
                                          type="text"
                                          name={`experience.${index}.country`}
                                          className="form-control"
                                          placeholder="Country"
                                          value={values.phone}
                                          onChange={handleChange}
                                        />
                                      </div>
                                    </div>

                                    <div className="row">
                                      <div className="col-md-6 form-group">
                                        <label for="profession">From</label>
                                        <input
                                          type="text"
                                          name={`experience.${index}.from`}
                                          className="form-control"
                                          placeholder="Start date"
                                          value={values.profession}
                                          onChange={handleChange}
                                        />
                                      </div>
                                      <div className="col-md-6 form-group">
                                        <label for="profession">To</label>
                                        <input
                                          type="text"
                                          name={`experience.${index}.to`}
                                          className="form-control"
                                          placeholder="End Date"
                                          value={values.profession}
                                          onChange={handleChange}
                                        />
                                      </div>
                                    </div>
                                    <hr style={{ color: "black" }} />
                                  </>
                                ))}
                              </div>
                            </>
                          )}
                        </FieldArray>
                      </div>
                      <div>
                        <FieldArray name="activeMembership">
                          {(fieldArrayProps) => (
                            <>
                              <div>
                                <label
                                  className="professionFormLebel col-md-11"
                                  style={{ padding: "0px" }}
                                >
                                  Active Membership
                                </label>
                                <span
                                  onClick={() => {
                                    fieldArrayProps.push({
                                      name: "",
                                      position: "",
                                      from: "",
                                    });
                                  }}
                                  className="updateprofilebutton"
                                  href="#0"
                                  style={{
                                    fontSize: "24px",
                                    margin: "0% 0% 0% 4%",
                                    padding: "0px 15px",
                                  }}
                                >
                                  +
                                </span>
                              </div>
                              {values.activeMembership.map((value, index) => (
                                <>
                                  <div className="row" key={index}>
                                    <div className="col-md-4 form-group">
                                      <label for="firstName">Name</label>
                                      <input
                                        type="text"
                                        name={`activeMembership.${index}.name`}
                                        className="form-control"
                                        placeholder="Membership title"
                                        onBlur={handleBlur}
                                        value={values.firstName}
                                        onChange={handleChange}
                                      />
                                    </div>
                                    <div className="col-md-4 form-group">
                                      <label for="lastName">Position</label>
                                      <input
                                        type="text"
                                        name={`activeMembership.${index}.position`}
                                        className="form-control"
                                        placeholder="Position"
                                        onBlur={handleBlur}
                                        value={values.lastName}
                                        onChange={handleChange}
                                      />
                                    </div>
                                    <div className="col-md-3 form-group">
                                      <label for="Phone">From </label>
                                      <input
                                        type="text"
                                        name={`activeMembership.${index}.from`}
                                        className="form-control"
                                        placeholder="Join Date"
                                        value={values.phone}
                                        onChange={handleChange}
                                      />
                                    </div>
                                  </div>
                                </>
                              ))}
                            </>
                          )}
                        </FieldArray>
                      </div>

                      <div>
                        <FieldArray name="awards">
                          {(fieldArrayProps) => (
                            <>
                              <div>
                                <label
                                  className="professionFormLebel col-md-11"
                                  style={{ padding: "0px" }}
                                >
                                  Awards & Achievements
                                </label>
                                <span
                                  onClick={() => {
                                    fieldArrayProps.push({
                                      name: "",
                                      body: "",
                                      from: "",
                                    });
                                  }}
                                  className="updateprofilebutton"
                                  href="#0"
                                  style={{
                                    fontSize: "24px",
                                    margin: "0% 0% 0% 4%",
                                    padding: "0px 15px",
                                  }}
                                >
                                  +
                                </span>
                              </div>
                              {values.awards.map((value, index) => (
                                <>
                                  <div className="row" key={index}>
                                    <div className="col-md-4 form-group">
                                      <label for="firstName">Name</label>
                                      <input
                                        type="text"
                                        name={`awards.${index}.name`}
                                        className="form-control"
                                        placeholder="Award Title"
                                        onBlur={handleBlur}
                                        value={values.firstName}
                                        onChange={handleChange}
                                      />
                                    </div>
                                    <div className="col-md-4 form-group">
                                      <label for="lastName">
                                        Awarding Body
                                      </label>
                                      <input
                                        type="text"
                                        name={`awards.${index}.body`}
                                        className="form-control"
                                        placeholder="Organization"
                                        onBlur={handleBlur}
                                        value={values.lastName}
                                        onChange={handleChange}
                                      />
                                    </div>
                                    <div className="col-md-3 form-group">
                                      <label for="Phone">From </label>

                                      <input
                                        type="text"
                                        name={`awards.${index}.from`}
                                        className="form-control"
                                        placeholder="Issue Date"
                                        value={values.phone}
                                        onChange={handleChange}
                                      />
                                    </div>
                                  </div>
                                </>
                              ))}
                            </>
                          )}
                        </FieldArray>
                      </div>

                      <button
                        type="submit"
                        className="btn btn-primary btn-hover"
                      >
                        Update
                      </button>
                    </form>
                  )}
                </Formik>
              </div>
            </div>
          </div>
        </div>
      ) : (
        <div className="row mb-none-30">
          <div className="col-md-12">
            <div className="blog-item  mb-30" style={{ paddingBottom: "2%" }}>
              <div
                className=" content"
                style={{ display: "flex", padding: "20px 30px" }}
              >
                <p className="profilelabel">
                  Profession Class <span style={{ margin: "0px 10px" }}>:</span>
                  <span className="profiledata">Job Holder</span>
                </p>
              </div>
              <div className="content" style={{ padding: "5px 30px" }}>
                <p className="profilelabel">
                  Experience{" "}
                  <span style={{ margin: "15px", padding: "0px" }}></span>
                </p>
              </div>
              <div
                className="content"
                style={{ padding: "0px 0px 0px 35px", display: "flex" }}
              >
                <div className="col-md-6">
                  <p className="professionLabel">
                    Organization <span>:</span>
                    <span className="profiledata">Idatahost</span>
                  </p>
                  <p className="professionLabel">
                    Industry <span>:</span>{" "}
                    <span className="profiledata">IT</span>
                  </p>
                  <p className="professionLabel">
                    From
                    <span>:</span> <span className="profiledata">1/2/2020</span>
                  </p>
                </div>
                <div className="col-md-6">
                  <p className="professionLabel">
                    Country <span>:</span>{" "}
                    <span className="profiledata">Bangladesh</span>
                  </p>
                  <p className="professionLabel">
                    Role <span>:</span>
                    <span className="profiledata">Developer</span>
                  </p>

                  <p className="professionLabel">
                    To
                    <span>:</span> <span className="profiledata">8/7/2021</span>
                  </p>
                </div>
              </div>

              <div className="content" style={{ padding: "5px 30px" }}>
                <p className="profilelabel">
                  Active Membership{" "}
                  <span style={{ margin: "15px", padding: "0px" }}></span>
                </p>
              </div>
              <div className="content" style={{ padding: "0px 0px 0px 35px" }}>
                <div style={{ display: "flex" }}>
                  <p className="professionLabel">
                    Name <span>:</span>
                    <span className="profiledata"> Computer Society</span>
                  </p>
                  <p className="professionLabel">
                    Position <span>:</span>{" "}
                    <span className="profiledata"> Associate</span>
                  </p>
                  <p className="professionLabel">
                    From
                    <span>:</span> <span className="profiledata">1/2/2020</span>
                  </p>
                </div>
              </div>

              <div className="content" style={{ padding: "5px 30px" }}>
                <p className="profilelabel">
                  Awards & Achievements{" "}
                  <span style={{ margin: "15px", padding: "0px" }}></span>
                </p>
              </div>
              <div className="content" style={{ padding: "0px 0px 0px 35px" }}>
                <div style={{ display: "flex" }}>
                  <p className="professionLabel">
                    Name <span>:</span>
                    <span className="profiledata">Best Employee</span>
                  </p>
                  <p className="professionLabel">
                    Time <span>:</span>{" "}
                    <span className="profiledata">3/2/2021</span>
                  </p>
                  <p className="professionLabel">
                    Awarding Body
                    <span>:</span>{" "}
                    <span className="profiledata">IdataHost</span>
                  </p>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
      {/* Professional Info End */}

      {/* Academic Heading Start */}
      <div className="row mb-none-30">
        <div className="col-md-12 ">
          <div className="blog-item mb-30" style={{ marginTop: "1%" }}>
            <div
              className="content promoBg "
              style={{ display: "flex", padding: "2% 2%" }}
            >
              <p className="col-md-11 promoText">Academic</p>
              <span
                className="promoselectionButton"
                href="#0"
                onClick={() => setisAcademicsUpdate(true)}
